INSERT INTO `article` VALUES('9','1','PCB设计讲座视频','','<p>&nbsp;&nbsp;&nbsp;&nbsp; PCB版图设计是电子设计中的重要组成部分，它是电路系统的承载体，其质量优劣直影响作品（产品）质量。</p><p>&nbsp;&nbsp;&nbsp;&nbsp; 由于课时少，故课程仅讲设计过程的主干！</p><p>&nbsp;&nbsp;&nbsp;&nbsp; 教师：李良荣<br /></p><p>&nbsp;&nbsp;&nbsp;&nbsp; 课程时间：2011年7月6日至8日</p><p>&nbsp;&nbsp;&nbsp;&nbsp; 视频已完成编辑，并上网。<br /></p><br />','PCB,PCB设计，PCB板','','','0','242','1310867309','','admin','','1','0');
INSERT INTO `article` VALUES('10','1','贵州赛区组织工作会议的通知','','<p align=\"center\"><strong>关于全国大学生电子设计竞赛贵州赛区</strong></p><p align=\"center\"><strong>组织工作会议的通知</strong></p><p><strong>&nbsp;</strong></p><p><strong>各参赛学校：</strong></p><p><strong>全国大学生电子设计竞赛贵州赛区组织委员会（以下简称赛区组委会）受贵州省教育厅委托，组织参加2011年全国大学生电子设计竞赛（以下简称竞赛），按照今年竞赛的进度安排，赛区组织委员会将于2011年8月16日在贵州大学北校区召开2011年赛区组织工作会议。具体通知如下：</strong></p><p><strong>&nbsp;</strong></p><p><strong>一、参会代表</strong></p><p><strong>各参赛学校主管竞赛的领导1名，参赛学校主持竞赛学生培训的教师1名。</strong></p><p><strong>二、会议安排</strong></p><p><strong>1、会议地点：贵州大学北校区2行政办公楼2楼4号会议室；</strong></p><p><strong>2、会议时间：8月16日上午9:30时会场签到； 10:00时开会。</strong></p><p><strong>三、会议议程：</strong></p><p><strong>1、赛区主办学校领导讲话；</strong></p><p><strong>2、传达全国主委会组织工作会议精神；</strong></p><p><strong>3、安排赛区本届赛事工作（含巡视员派遣工作等，巡视员8月29日下午3:00到贵州大学北校区博学楼512S开会，相关学校按其赛场数派遣巡视员）；</strong></p><p><strong>4、教育厅领导讲话。</strong></p><p><strong>&nbsp;</strong></p><p align=\"left\"><strong>注：本次会议差旅食宿自理，会议无补助。</strong></p><p align=\"left\"><strong>&nbsp;</strong></p><p align=\"center\"><strong>全国大学生电子设计竞赛贵州赛区组织委员会</strong></p><p align=\"center\"><strong>2011年7月29日</strong></p><p align=\"center\"><strong></strong>&nbsp;</p><br />','贵州,电子竞赛,通知','','','1','159','1311993792','','admin','','1','0');
